import './App.css';
import React, { useState, useEffect, useRef } from 'react'

function App() {
  const [fixedClass, setFixedClass] = useState('link-wrap');
  const [currentView, setCurrentView] = useState('home');
  const aboutRef = useRef();

  useEffect(() => {
    window.addEventListener('scroll', fixNavbar);
    return () => window.removeEventListener('scroll', fixNavbar);
  }, []);

  const fixNavbar = () => {
    if (window !== undefined) {
      const maxHeight = window.innerHeight;
      let windowHeight = window.scrollY;
      // window height changed for the demo
      console.log(window.innerHeight);
      windowHeight >= maxHeight ? setFixedClass('link-wrap-fixed') : setFixedClass('link-wrap');
      if(windowHeight < maxHeight) {
        setCurrentView('home');
      } else if (windowHeight > maxHeight && windowHeight< 2*maxHeight){
        setCurrentView('about')
      }
    }
  };
  const handleKnowMore = () => {
    aboutRef.current.scrollIntoView({ behavior: 'smooth' });
  }

  return (
    <div className="App">
      <section className="home">
        <div className="canvas"></div>
        <div className="text">Ol√°, me chamo
          <span className="highlight"> Kelvin Pichinini</span>.
          <br />
          Desenvolvedor Web FullStack. 
        </div>
        <button className="button" onClick={ handleKnowMore }>Conhecer mais</button>
      <nav>
        <div className={ fixedClass }>
          <div className="link-btn active">home</div>
          <div className="link-btn">sobre</div>
          <div className="link-btn">projetos</div>
          <div className="link-btn">contato</div>
        </div>
      </nav>
      </section>
      <section className="about" ref={aboutRef}>
        <div className="title"> SOBRE </div>
        <div className="bar"></div>
      </section>
    </div>
  );
}

export default App;
