import './App.css';
import logo from "./assets/logo.png"
import result1 from "./assets/result1.jpeg"
import result2 from "./assets/result2.jpeg"
import membro1 from './assets/membro1.jpeg';
import membro2 from './assets/membro2.jpeg';
import membro3 from './assets/membro3.jpeg';
import membro4 from './assets/membro4.jpeg';
import membro5 from './assets/membro5.jpeg';
import membro6 from './assets/membro6.jpeg';
import membro7 from './assets/membro7.jpeg';
import membro8 from './assets/membro8.jpeg';
import membro9 from './assets/membro9.jpeg';
import membro10 from './assets/membro10.jpeg';
import depo1 from './assets/depo1.jpeg';
import depo2 from './assets/depo2.jpeg';
import depo3 from './assets/depo3.jpeg';
import depo4 from './assets/depo4.jpeg';
import depo5 from './assets/depo5.jpeg';
import depo6 from './assets/depo6.jpeg';
import depo7 from './assets/depo7.jpeg';
import React, { useState, useEffect, useRef } from 'react'
import { polyfill } from "seamless-scroll-polyfill";
import PlanCards from './components/PlanCards';
import 'bootstrap/dist/css/bootstrap.css';
import Carousel from 'react-bootstrap/Carousel';

polyfill();

function App() {
  const [fixedClass, setFixedClass] = useState('link-wrap');
  const [currentView, setCurrentView] = useState('home');
  const homeRef = useRef();
  const aboutRef = useRef();
  const resultsRef = useRef();
  const FAQRef = useRef();
  const plansRef = useRef();

  const membersProfit = [
    membro1,
    membro2,
    membro3,
    membro4,
    membro5,
    membro6,
    membro7,
    membro8,
    membro9,
    membro10,
  ]

  const membersDepo = [
    depo1,
    depo2,
    depo3,
    depo4,
    depo5,
    depo6,
    depo7,
  ]


  useEffect(() => {
    window.addEventListener('scroll', fixNavbar);
    return () => window.removeEventListener('scroll', fixNavbar);
  }, []);

  const fixNavbar = () => {
    if (window !== undefined) {
      const maxHeight = window.innerHeight;
      let windowHeight = window.scrollY;
      windowHeight >= maxHeight ? setFixedClass('link-wrap-fixed') : setFixedClass('link-wrap');
      if(windowHeight < maxHeight) {
        setCurrentView('homeRef');
      } else if (windowHeight >= maxHeight && windowHeight < 2*maxHeight){
        setCurrentView('aboutRef');
      } else if (windowHeight >= 2* maxHeight && windowHeight < 3*maxHeight){
        setCurrentView('resultsRef')
      }else if (windowHeight >= 3* maxHeight && windowHeight < 4*maxHeight){
        setCurrentView('plansRef')
      }else if (windowHeight >= 4* maxHeight && windowHeight < 5*maxHeight){
        setCurrentView('FAQRef')
      }

    }
  };
  const handleClick = (event) => {
    const ref = event.target.className;
    if(ref.includes('aboutRef')) {
      aboutRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    if(ref.includes('homeRef')) {
      homeRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    if(ref.includes('resultsRef')) {
      resultsRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    if(ref.includes('plansRef')) {
      plansRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    if(ref.includes('FAQRef')) {
      FAQRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }

  return (
    <div className="App">
      <section className="home" ref={homeRef}>
        <div className="canvas"></div>
        <img className="cover-logo"src={ logo } alt="" />
        <div className="text">
          Faça parte do grupo do trader 
          <span className='highlight'> TOP 15 MUNDIAL</span>  do FIFA Ultimate Team.
          <br /> Monte o seu time perfeito! 
        </div>
        <div className='home-btns'>
          <button className="aboutRef button" onClick={ handleClick }>Saiba mais</button>
          <button className="plansRef button" onClick={ handleClick }>Planos </button>
        </div>
      <nav>
        <div className={ fixedClass }>
          <button className={`aboutRef link-btn ${currentView.includes('about') ? 'active' : ''}`} onClick={ handleClick }>SOBRE</button>
          <button className={`resultsRef link-btn ${currentView.includes('results') ? 'active' : ''}`} onClick={ handleClick }>RESULTADOS</button>
          <button className={`plansRef link-btn ${currentView.includes('plans') ? 'active' : ''}`} onClick={ handleClick }>PLANOS</button>
          <button className={`FAQRef link-btn ${currentView.includes('FAQ') ? 'active' : ''}`} onClick={ handleClick }>DÚVIDAS</button>
        </div>
      </nav>
      </section>
      <section className="content-main" ref={aboutRef}>
        <div className="title"> SOBRE </div>
        <div className="bar"></div>
        <div className="about-text">
          <h3>
            Seja bem vindo a minha página! 
          </h3>
          <p>
          <br />
            Sou o “Guerreiro - FUT Trader”, negocio no mercado desde o FIFA 17.
            Fiz mais de <span className='highlight2'> 90 milhões </span>  de coins no FIFA 21, fui top 15 do mundo no FIFA 22 com mais de <span className='highlight2'> 130 milhões </span> de lucro e estou aqui para fazer você ter o seu time dos sonhos sem precisar de FIFA POINTS.
              <br />
              <br />
            Se você sempre quis testar qualquer carta do jogo ou então levantar a maior quantidade de coins possível para vender,
             você está no lugar certo. Com trade e gestão você chegará lá.
             <br />
             <br />

             <span className='highlight2'> Não dependa de premiações e sorte nos packs. </span> 
             <br />
             <br />
             No meu grupo você poderá aprender todos os conceitos de trade através de inúmeros guias ou apenas seguir todas as minhas calls dos mais variados tipos de investimento. 

            Além disso, terá acesso a um grupo de moderadores que te darão suporte praticamente 24hrs por dia e também receberá análises sobre gameplay produzidas por um coach profissional com um playoff em Londres no currículo. 
          </p>
          <h4>
          <br />
          <br />
            Não perca tempo, assine já!

          </h4>
        </div>
      </section>
      <section className="content-main" ref={resultsRef}>
        <div className="title"> RESULTADOS E DEPOIMENTOS </div>
        <div className="bar"></div>
        <h3>Meus resultados</h3>
        <div className="my-results">
          <div className="result">
            <h4 className="result-title"> FIFA 22: TOP 15 GLOBAL</h4>
            <img src={ result1 } alt="" className='result-img'/>
          </div>
          <div className="result">
            <h4 className="result-title"> FIFA 21: 42 MILHÕES de lucro em 1 mês - TOP 2 GLOBAL</h4>
            <img src={ result2 } alt="" className='result-img'/>
          </div>
        </div>
        <h3 className='carousel-title'>Lucro dos membros no <span className='highlight'>PRIMEIRO</span> mês do fifa 23</h3>
        <Carousel slide={false} interval={1500}>
          { membersProfit.map((url, i) => (
            <Carousel.Item key={i}>
              <div className='carousel-bg'>
                <img className="carousel-img" src={ url } alt=""/>
              </div>
            </Carousel.Item>
            ))
          }

        </Carousel>
        <h3 className='carousel-title'>Depoimentos dos membros durante a temporada 22</h3>
        <Carousel slide={false} interval={5000}>
          { membersDepo.map((url, i) => (
            <Carousel.Item key={i}>
              <div className='carousel-bg'>
                <img className="carousel-img" src={ url } alt=""/>
              </div>
            </Carousel.Item>
            ))
          }

        </Carousel>
      </section>
      <section className="content-main" ref={plansRef}>
        <div className="title"> PLANOS </div>
        <div className="bar"></div>
        <PlanCards />
      </section>
      <section className="content" ref={FAQRef}>
        <div className="title"> DÚVIDAS FREQUENTES </div>
        <div className="bar"></div>
      </section>
    </div>
  );
}

export default App;
